import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import Dropzone from 'react-dropzone';
import './index.css';
// const { imageData } = require('../../matcha_pb');


class InitialPhoto extends Component {
	// readFile = (file) => {
	// 	return new Promise((resolve, reject) => {
	// 		let reader = new FileReader()
	// 		reader.readAsDataURL(file)
	// 		reader.onload = () => {
	// 			console.log({
	// 				data: reader.result
	// 			})
	// 		}
	// 		resolve(reader);
	// 	})
	// }
	constructor(props) {
		super(props);
		this.state = {
			aaaa: ''
		}
	}

	onDrop = async (acceptedFiles) => {
		const file = acceptedFiles.find(f => f)
		const i = new Image()
		// newimage makes <img src="">
		let promise = new Promise((resolve, reject) => {
			let reader = new FileReader()
			reader.readAsDataURL(file)
			reader.onload = () => {
				console.log({
					data: reader.result
				})
				const arr = reader.result.split(",");
			}
			resolve(arr[1]);
		})
		this.setState({ aaaa: arr[1] })

		console.log("aaaa ", this.state.aaaa);
		// const reader = await promise;
		// let secPromise = new Promise((resolve, reject) => {
		// 	const arr = reader.result.split(",")
		// 	resolve(arr);
		// })
		// const passByte = await secPromise;
		// console.log("so.. ", passByte[1]);
		// const arr = reader.result.split(",");
		// console.log("arr is ", arr);
		// i.onload = () => {
		// 	let reader = new FileReader()
		// 	reader.readAsDataURL(file)
		// 	reader.onload = () => {
		// 		console.log({
		// 			src: file.preview,
		// 			width: i.width,
		// 			height: i.height,
		// 			data: reader.result
		// 		})
		// 	}
		// }

		// make it to async function so arr is created after that follow the webpage

		i.src = file.preview;
		// console.log(i.src)
	}

	render() {
		const maxSize = 5242880;
		return (
			<div className="text-center mt-5">
				<Dropzone
					onDrop={this.onDrop}
					accept="image/png, image/jpeg, image/jpg"
					minSize={0}
					maxSize={maxSize}
					multiple
				>
					{({getRootProps, getInputProps, isDragActive, isDragReject, rejectedFiles}) => {
						const isFileTooLarge = rejectedFiles.length > 0 && rejectedFiles[0].size > maxSize;
						return (
							<div {...getRootProps()}>
								<input {...getInputProps()} />
								{!isDragActive && 'Click here or drop a file to upload!'}
								{isDragActive && !isDragReject && "Drop it like it's hot!"}
								{isDragReject && "File type not accepted, sorry!"}
								{isFileTooLarge && (
									<div className="text-danger mt-2">
										File is too large.
									</div>
								)}
							</div>
						)}
					}
				</Dropzone>
			</div>
		);
	}
}

export default withCookies(InitialPhoto);
