import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import Dropzone from 'react-dropzone';
import './index.css';
// const { imageData } = require('../../matcha_pb');

class InitialPhoto extends Component {
	constructor(props) {
		super(props);
		this.state = {
			names: [],
			images: []
		}
	}

	onDrop = async (acceptedFile) => {
		const file = acceptedFile.find(f => f)
		let joined = this.state.names.concat(file.name);
		this.setState({ names: joined });
		// newimage makes <img src="">
		let promise = new Promise((resolve, reject) => {
			let reader = new FileReader()
			reader.readAsDataURL(file)
			reader.onload = () => {
				resolve(reader.result);
			}
		})
		const result = await promise;
		const arr = result.split(",");
		joined = this.state.images.concat(arr[1]);
		this.setState({ images: joined });
		console.log("names are ", this.state.names);
		console.log("images are ", this.state.images);
	}

	render() {
		const maxSize = 5242880;
		return (
			<div className="text-center mt-5">
				<Dropzone
					onDrop={this.onDrop}
					accept="image/png, image/jpeg, image/jpg"
					minSize={0}
					maxSize={maxSize}
				>
					{({getRootProps, getInputProps, isDragActive, isDragReject, rejectedFiles}) => {
						const isFileTooLarge = rejectedFiles.length > 0 && rejectedFiles[0].size > maxSize;
						return (
							<div {...getRootProps()}>
								<input {...getInputProps()} />
								{!isDragActive && 'Click here or drop a file to upload!'}
								{isDragActive && !isDragReject && "Drop it like it's hot!"}
								{isDragReject && "File type not accepted, sorry!"}
								{isFileTooLarge && (
									<div className="text-danger mt-2">
										File is too large.
									</div>
								)}
							</div>
						)}
					}
				</Dropzone>
				<div>
					{this.state.names.map((value, i) => {
						return <div key={i}>{value}</div>
					})}
				</div>
			</div>
		);
	}
}

export default withCookies(InitialPhoto);
