import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { withCookies } from 'react-cookie';
import './index.css';
const { User } = require('../matcha_pb');

class InitialProfile extends Component {
	constructor(props) {
		super(props);
		this.state = {
			gender: '',
			sexPreference: '',
			biography: '',
			value: '',
			interests: [],
			uid: '',
			session_id: '',
			location: ''
		}
	}

	getPermission = () => {
		return new Promise((resolve, reject) => {
			console.log(`componentdidmount`);
			const { cookies } = this.props;
			const uid = cookies.get('uid');
			const session_id = cookies.get('session_id');
			this.setState({ uid, session_id});
			let location = false;
			if (window.confirm(`Allow 'Matcha' to use your location?`)) {
				location = true;
			}
			cookies.set('location', location, { path: '/' });
			this.setState({ location });
			console.log(`location is ${cookies.get('location')}`);
		})
	}

	position = async () => {
		const test = await this.getPermission();
		console.log(`funcrionposition`);
		navigator.geolocation.getCurrentPosition(
			async position => {
				this.setState({
					latitude: position.coords.latitude,
					longitude: position.coords.longitude
				})
				const config = {
					api_key: '39203097de53283e10880e0023185633d31d395'
				};
				if (this.state.location) {
					const result = this.state.result = await axios.get(`https://api.geocod.io/v1.3/reverse?q=${position.coords.latitude},${position.coords.longitude}&api_key=${config.api_key}`);
					console.log(`result is ${result}`);
				} else {
					const ipAdd = await axios.get(`https://api.ipify.org/`);
					const result = await axios.get(`https://json.geoiplookup.io/${ipAdd.data}`);
					console.log(`result2 is', ${result}`);
				}
			},
				err => console.log(err)
		);
	}

	addProfileInfo = () => {
		const request = new User();
		const metaData = {
			'user_id': this.state.uid,
			'session_id': this.state.session_id
		}
		this.props.cookies.remove('missing_profile');
		request.setGender(this.state.gender);
		request.setPreference(this.state.sexPreference);
		request.setBio(this.state.biography);
		request.setId(this.state.uid);
		for (const interest in this.state.interests) {
			request.addTags(this.state.interests[interest]);

		}
		window.Aclient.updateUser(request, metaData, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
			} else {
				console.log(reply.getMessage());
			}
		})
	}

	addInterests = () => {
		this.setState(state => {
			const interests = state.interests.concat(state.value);

			return {
				interests,
				value: '',
			};
		});
	};

	render() {
		this.position();
		return (
			<div>
				<div className="header">
					<div id="title">MATCH<span className="red">A</span></div>
					<div><Link to="/" className="loginheader">Logout</Link></div>
				</div>
				<div className="rest">
					<div className="tellusmore">Tell Us More about Yourself</div>
					<form
						className="toggle"
						onSubmit={e => {
							e.preventDefault();
							this.addProfileInfo();
						}}
					>
						<div className="areyou">
							<div className="leftmargin">Are you</div>
							<div className="areyoulabels">
								<input
									type="radio"
									value="female"
									id="female"
									name="gender"
									onChange={e => this.setState({ gender: e.target.value })}
									// checked
									/>
								<label htmlFor="female">Female</label>
								<input
									type="radio"
									value="male"
									id="male"
									onChange={e => this.setState({ gender: e.target.value })}
									name="gender"
								/>
								<label htmlFor="male">Male</label>
							</div>
						</div>
						<div className="interested">
							<div className="leftmargin">Who are you instered in?</div>
							<div className="interestedlables">
								<input
									type="radio"
									value="prefFemale"
									id="prefFemale"
									name="sexpref"
									onChange={e => this.setState({ sexPreference: e.target.value })}
									/>
								<label htmlFor="prefFemale">Female</label>
								<input
									type="radio"
									value="prefMale"
									id="prefMale"
									onChange={e => this.setState({ sexPreference: e.target.value })}
									name="sexpref"
								/>
								<label htmlFor="prefMale">Male</label>
								<input
									type="radio"
									value="prefBoth"
									id="prefBoth"
									onChange={e => this.setState({ sexPreference: e.target.value })}
									name="sexpref"
								/>
								<label htmlFor="prefBoth">Both</label>
							</div>
						</div>
						<div className="bio">
							<div className="leftmargin">Bio</div>
							<textarea
								onChange={e => this.setState({ biography: e.target.value })}
							></textarea>
						</div>
						<div className="interest">
							<div className="leftmargin">Interests</div>
							<input
								type="text"
								value={this.state.value}
								onChange={e => this.setState({ value: e.target.value })}
							/>
							<button
								type="button"
								onClick={this.addInterests}
								disabled={!this.state.value}
							>
								Add
							</button>
							<div>
								{this.state.interests.map((value, i) => {
								return <span key={i}>{value}</span>
							})}
							</div>
						</div>
						<button type="submit">Save</button>
					</form>
				</div>
			</div>
		)
	}
}

export default withCookies(InitialProfile);
