import React, { Component } from 'react';
const { AccountClient } = require('../matcha_grpc_web_pb');
const { sendEmailRequest } = require('../matcha_pb');

class LostPasswod extends Component {
	constructor(props) {
		super(props);
		this.state = {
			email: ''
		}
	}

	sendResetPasswordEmail = () => {
		const client = new AccountClient('http://10.43.1.110:8080');
		const request = new sendEmailRequest();
		request.setEmail(this.state.email);
		client.sendPassResetEmail(request, {}, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
			} else {
				console.log(reply.getMessage());
			}
		})
	}

	render() {
		return (
			<div>
				<div>Forgot Your Password?</div>
				<form
					onSubmit={e => {
						e.preventDefault()
						this.sendResetPasswordEmail()
					}}
				>
					<input
						required
						id="emailforlostpass"
						type="email"
						placeholder="Email Address"
						value={this.state.email}
						onChange={e => this.setState({ email: e.target.value })}
					/>
					<label for="emailforlostpass">Enter your email address to reset your password.</label>
				</form>
			</div>
		)
	}
}

export default LostPasswod;
