import React, { Component } from 'react';
const { AccountClient } = require('../matcha_grpc_web_pb');
const { resetPassRequest } = require('../matcha_pb');

export default class ResetPassword extends Component {
	constructor(props) {
		super(props);

		this.state = {
			newPass1: '',
			newPass2: ''
		}
	}

	resetPass = () => {
		const client = new AccountClient('http://10.43.1.110:8080');
		const request = new resetPassRequest();
		const pathArray = window.location.search;
		const urlParams = new URLSearchParams(pathArray);
		const updatedEmail = urlParams.get('email');
		const updatedHash = urlParams.get('hash');
		request.setEmail(updatedEmail);
		request.setHash(updatedHash);
		request.setNewpass(this.state.newPass);
		client.forgotPassword(request, {}, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
			} else {
				console.log(reply.getMessage());
			}
		})
	}

	// passwordMatches = (p1, p2) => {
	// 	if (p1 && p2 && p1 === p2) {
	// 		return 'passwords matched =)';
	// 	} else if (p1 && p2 && p1 !== p2) {
	// 		return 'passwords do not match =(';
	// 	}
	// }

	render() {
		return (
			<div>
				<form
					onSubmit={e => {
						e.preventDefault()
						console.log(this.state.newPass2);
						if (this.state.newPass1 === this.state.newPass2) {
							this.resetPass()
						}
					}}
				>
					<input
						required
						id="resetpass"
						type="password"
						placeholder="New Password"
						value={this.state.newPass1}
						onChange={e => this.setState({ newPass1: e.target.value })}
					/>
					<input
						required
						id="resetpassconfirm"
						type="password"
						placeholder="Confirm New Password"
						value={this.state.newPass2}
						onChange={e => this.setState({ newPass2: e.target.value })}
					/>
					<button
						type="submit"
					>
						Submit
					</button>
				</form>
			</div>
		)
	}
}
