import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
const { User } = require('../matcha_pb');

class MyPage extends Component {
	constructor(props) {
		super(props);

		this.state = {
			username: '',
			firstName: '',
			lastName: '',
			gender: '',
			preference: '',
			interests: [],
			bio: '',
			age: 0,
			pics: [],
			fameRating: 0,
			location: '',
			seenHistory: [],
			data: []
		}
	}

	componentDidMount() {
		this.getUserInfo();
		this.allSet();
	}

	getUserInfo = () => {
		const uid = this.props.cookies.get('uid');
		const temp = this.props.cookies.get('session_id');
		const request = new User();
		request.setId(uid);
		let metadata = {
			'user_id': uid,
			'session_id': temp
		}
		window.Aclient.getUser(request, metadata, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
			} else {
				this.setState({
					username: reply.getUsername(),
					firstName: reply.getFirstname(),
					lastName: reply.getLastname(),
					gender: reply.getGender(),
					preference: reply.getPreference(),
					age: reply.getAge(),
					fameRating: reply.getFamerating(),
					location: reply.getLocation(),
					interests: reply.getTagsList(),
					bio: reply.getBio(),
					seenHistory: reply.getSeenhistoryList()
				});
			}
		})
	}

	getImages = () => {
		return new Promise((resolve, reject) => {
			const request = new imageRequest();
			let arr = [];
			const uid = this.props.cookies.get('uid');
			const temp = this.props.cookies.get('session_id');
			const metaData = {
				'user_id': uid,
				'session_id': temp
			}
			request.setUserid(3);
			request.setCount(2);
			const stream = window.Aclient.getImages(request, metaData);
			stream.on('data', function(response) {
				console.log(response.getImage_asB64());
				const d = response.getImage_asB64();
				arr.push(d);
			});
			stream.on('error', function(err) {
				console.log(`error: ${err}`);
			});
			stream.on('end', function(){
				resolve(arr);
			})
		})
	}

	allSet = async () => {
		try {
			const data = await this.getImages();
			this.setState({data});
		} catch(error) {
			console.log(error.response);
		}
	}

	render() {
		return (
			<div>
				<div>{this.state.username}</div>
				<div>{this.state.firstName}</div>
				<div>{this.state.lastName}</div>
				<div>{this.state.gender}</div>
				<div>{this.state.preference}</div>
				<div>{this.state.age}</div>
				<div>{this.state.fameRating}</div>
				<div>{this.state.location}</div>
				<div>{this.state.interests.map((value, i) => {
					return <span key={i}>{value} </span>
				})}</div>
				<div>{this.state.bio}</div>
				<div>{this.state.seenHistory}</div>
			</div>
		)
	}
}

export default withCookies(MyPage);
