import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { withCookies } from 'react-cookie';
import './index.css';
const { AccountClient } = require('../matcha_grpc_web_pb');
const { loginRequest } = require('../matcha_pb');

class Login extends Component {
	constructor(props) {
		super(props);

		this.state = {
			username: '',
			password: ''
		};
	}

	letLoginUser = () => {
		// const client = new AccountClient('http://localhost:8080');
		const client = new AccountClient('http://192.168.43.23:8080');
		const request = new loginRequest();
		request.setUsername(this.state.username);
		request.setPassword(this.state.password);
		var metadata = {
			'custom-header-1': 'value1',
			'test': 'value2'
		};
		client.loginUser(request, metadata, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
			} else {
				console.log(reply.getMessage());
				const { cookies } = this.props;
				const token = 'test';
				const uid = '1';
				cookies.set('token', token, { path: '/' });	
				cookies.set('uid', uid, { path: '/' });
				console.log(cookies);
			}
		})
	}

	render() {
		return (
			<div className="loginheader">
				<div id="title"><Link to="/">MATCH<span className="red">A</span></Link></div>
				<div></div>
				<div className="login">
					<form
						onSubmit={e => {
							e.preventDefault()
							this.letLoginUser()
						}
					}>
						<input
							type="text"
							placeholder="Username"
							onChange={e => this.setState({ username: e.target.value })}
							/>
						<input
							type="password"
							placeholder="Password"
							onChange={e => this.setState({ password: e.target.value })}
						/>
						<button
							type="submit"
							id="loginbutton"
						>
							Log In to Matcha
						</button>
					</form>
					<div className="notyet">Not a member yet? <Link to="/signup">Sign Up</Link> -></div>
				</div>
			</div>
		);
	}
}

export default withCookies(Login);
