import React, { Component } from 'react';
import { withCookies } from 'react-cookie'
const { AccountClient } = require('../matcha_grpc_web_pb');
const { empty } = require('../matcha_pb');

class MyPage extends Component {
	constructor(props) {
		super(props);

		this.state = {
			username: '',
			firstName: '',
			lastName: '',
			gender: '',
			preference: '',
			bio: ''
		}

		this.updateDimensions = this.updateDimensions.bind(this);
	}

  componentDidMount() {
    this.updateDimensions();
    window.addEventListener("resize", this.updateDimensions);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.updateDimensions);
  }

  updateDimensions() {
    let windowWidth = typeof window !== "undefined" ? window.innerWidth : 0;
    let windowHeight = typeof window !== "undefined" ? window.innerHeight : 0;

    this.setState({ windowWidth, windowHeight });
  }

	componentDidMount() {
		const client = new AccountClient('http://192.168.43.23:8080');
		const uid = this.props.cookies.get('uid');
		const temp = this.props.cookies.get('session_id');
		const request = new empty();
		let metadata = {
			'user_id': uid,
			'session_id': temp
		}
		client.getSelf(request, metadata, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
				this.props.history.push('/login');
			} else {
				// console.log(`aa: ${reply.getGender().Gender}`);
				this.setState({
					username: reply.getUsername(),
					firstName: reply.getFirstname(),
					lastName: reply.getLastname(),
					gender: reply.getGender(),
					preference: reply.getPreference(),
				});
				// console.log(this.state);
			}
		})
	}

	render() {
		return (
			<div>
				{this.state.username}
			</div>
		)
	}
}

export default withCookies(MyPage);
