import React, { Component } from 'react';
import { withCookies } from 'react-cookie'
const { AccountClient } = require('../matcha_grpc_web_pb');
const { User } = require('../matcha_pb');

class MyPage extends Component {
	constructor(props) {
		super(props);

		this.state = {
			username: '',
			firstName: '',
			lastName: '',
			gender: '',
			preference: '',
			bio: '',
			windowWidth: 0,
			windowHeight: 0
		}

		// this.updateDimensions = this.updateDimensions.bind(this);
	}

	componentDidMount() {
		this.getUserInfo();
	}

	getUserInfo = () => {
		const client = new AccountClient('http://10.43.1.110:8080');
		const uid = this.props.cookies.get('uid');
		const temp = this.props.cookies.get('session_id');
		const request = new User();
		request.setId(uid);
		let metadata = {
			'user_id': uid,
			'session_id': temp
		}
		client.getUser(request, metadata, (err, reply) => {
			if (err) {
				console.log(err.code);
				console.log(err.message);
				// this.props.history.push('/login');
			} else {
				this.setState({
					username: reply.getUsername(),
					firstName: reply.getFirstname(),
					lastName: reply.getLastname(),
					gender: reply.getGender(),
					preference: reply.getPreference(),
				});
			}
		})
	}

	render() {

		return (
			<div>
				{this.state.username}
			</div>
		)
	}
}

export default withCookies(MyPage);
