import './index.css';

class Verify extends Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			hash: ''
		}
	}

	componentDidMount(){
		this.test();
	} 

	test = () => {
		const pathArray = window.location.search;
		const urlParams = new URLSearchParams(pathArray);
		const updatedEmail = urlParams.get('email');
		const updatedHash = urlParams.get('hash');
		console.log(updatedEmail);
		console.log(updatedHash);
		this.setState({ email: updatedEmail });
		this.setState({ hash: updatedHash });
		console.log(this.state.email);
		console.log(this.state.hash);
	}

	render() {
		return (
			<div>hi</div>
		)
	}
}

